slideshow=\"66980377\" href=\'http://localhost/wordpress/wp-content/uploads/2018/08/8039272610_d8a0713181_c-1.jpg\'><img width=\"150\" height=\"150\" src=\"http://localhost/wordpress/wp-content/uploads/2018/08/8039272610_d8a0713181_c-1-150x150.jpg\" alt=\"\" srcset=\"http://localhost/wordpress/wp-content/uploads/2018/08/8039272610_d8a0713181_c-1-150x150.jpg 150w, http://localhost/wordpress/wp-content/uploads/2018/08/8039272610_d8a0713181_c-1-100x100.jpg 100w\" sizes=\"(max-width: 150px) 100vw, 150px\" /></a>\n			</figure><figure class=\'gallery-item\'>\n				<a data-elementor-open-lightbox=\"default\" data-elementor-lightbox-slideshow=\"66980377\" href=\'http://localhost/wordpress/wp-content/uploads/2018/08/imageggs-1.jpg\'><img width=\"150\" height=\"150\" src=\"http://localhost/wordpress/wp-content/uploads/2018/08/imageggs-1-150x150.jpg\" alt=\"\" srcset=\"http://localhost/wordpress/wp-content/uploads/2018/08/imageggs-1-150x150.jpg 150w, http://localhost/wordpress/wp-content/uploads/2018/08/imageggs-1-100x100.jpg 100w\" sizes=\"(max-width: 150px) 100vw, 150px\" /></a>\n			</figure>\n			<h2>HOW WE DO IT</h2>		\n										<img width=\"679\" height=\"1000\" src=\"http://localhost/wordpress/wp-content/uploads/2018/08/120af9bebdd0ee926bc8c9f6d8252405.jpg\" alt=\"\" srcset=\"http://localhost/wordpress/wp-content/uploads/2018/08/120af9bebdd0ee926bc8c9f6d8252405.jpg 679w, http://localhost/wordpress/wp-content/uploads/2018/08/120af9bebdd0ee926bc8c9f6d8252405-204x300.jpg 204w\" sizes=\"(max-width: 679px) 100vw, 679px\" />											\n				<h3>\n					Assemble\n				</h3>\n				<p>Collect necessary data about institutions in order to categorize.</p>\n				<h3>\n					AID\n				</h3>\n				<p>Cooperation with the   institutions to help them grow.</p>\n				<h3>\n					Deliver\n				</h3>\n				<p>Deliver to people who need it the most.</p>\n			<h3>MEET OUR TEAM</h3>		\n		<p>Every one of our team members is devoted to delivering the finest possible solutions.</p>		\n			<figure><img width=\"200\" height=\"300\" src=\"http://localhost/wordpress/wp-content/uploads/2018/08/photo-1515462277126-2dd0c162007a-2-200x300.jpg\" alt=\"\" srcset=\"http://localhost/wordpress/wp-content/uploads/2018/08/photo-1515462277126-2dd0c162007a-2-200x300.jpg 200w, http://localhost/wordpress/wp-content/uploads/2018/08/photo-1515462277126-2dd0c162007a-2-768x1150.jpg 768w, http://localhost/wordpress/wp-content/uploads/2018/08/photo-1515462277126-2dd0c162007a-2-684x1024.jpg 684w, http://localhost/wordpress/wp-content/uploads/2018/08/photo-1515462277126-2dd0c162007a-2.jpg 1000w\" sizes=\"(max-width: 200px) 100vw, 200px\" /></figure><h3>Mahbubur Rahman<br></h3><p>Developer</p>		\n			<figure><img width=\"200\" height=\"300\" src=\"http://localhost/wordpress/wp-content/uploads/2018/08/12993509_1062434300482946_4438259694667478420_n-1-200x300.jpg\" alt=\"\" srcset=\"http://localhost/wordpress/wp-content/uploads/2018/08/12993509_1062434300482946_4438259694667478420_n-1-200x300.jpg 200w, http://localhost/wordpress/wp-content/uploads/2018/08/12993509_1062434300482946_4438259694667478420_n-1.jpg 640w\" sizes=\"(max-width: 200px) 100vw, 200px\" /></figure><h3>Md Shamsujjaman</h3><p>Developer</p>		\n			<figure><img width=\"240\" height=\"300\" src=\"http://localhost/wordpress/wp-content/uploads/2018/08/38837030_480771859062856_5507308431901458432_n-240x300.jpg\" alt=\"\" srcset=\"http://localhost/wordpress/wp-content/uploads/2018/08/38837030_480771859062856_5507308431901458432_n-240x300.jpg 240w, http://localhost/wordpress/wp-content/uploads/2018/08/38837030_480771859062856_5507308431901458432_n.jpg 768w\" sizes=\"(max-width: 240px) 100vw, 240px\" /></figure><h3>Radiah Tahsin Tanisha</h3><p>Developer</p>		\n			<figure><img width=\"225\" height=\"300\" src=\"http://localhost/wordpress/wp-content/uploads/2018/08/14199232_1605338049766332_8166335682618581288_n-1-225x300.jpg\" alt=\"\" srcset=\"http://localhost/wordpress/wp-content/uploads/2018/08/14199232_1605338049766332_8166335682618581288_n-1-225x300.jpg 225w, http://localhost/wordpress/wp-content/uploads/2018/08/14199232_1605338049766332_8166335682618581288_n-1.jpg 720w\" sizes=\"(max-width: 225px) 100vw, 225px\" /></figure><h3>Sumaiya Afroze Keya</h3><p>Developer</p>		\n							<a href=\"\" target=\"_blank\">\n					Facebook\n				</a>\n							<a href=\"\" target=\"_blank\">\n					Twitter\n				</a>\n							<a href=\"\" target=\"_blank\">\n					Google-plus\n				</a>\n							<a href=\"\" target=\"_blank\">\n					Linkedin\n				</a>', 'About.', '', 'inherit', 'closed', 'closed', '', '567-revision-v1', '', '', '2018-09-04 06:22:47', '2018-09-04 06:22:47', '', 567, 'http://localhost/wordpress/blog/2018/09/04/567-revision-v1/', 0, 'revision', '', 0),
(623, 1, '2018-09-04 06:29:40', '2018-09-04 06:29:40', '/**\r\n * Custom shortcode to display form entries in a table.\r\n *\r\n * Usage [wpforms_entries_table id=\"FORMID\"]\r\n *\r\n * @param array $atts\r\n */\r\nfunction wpf_dev_entries_table( $atts ) {\r\n	$atts = shortcode_atts( array(\r\n		\'id\' => \'\'\r\n	), $atts );\r\n	if ( empty( $atts[\'id\'] ) || !function_exists( \'wpforms\' ) ) {\r\n		return;\r\n	}\r\n	$form = wpforms()->form->get( absint( $atts[\'id\'] ) );\r\n	if ( empty( $form ) ) {\r\n		return;\r\n	}\r\n	$form_data = !empty( $form->post_content ) ? wpforms_decode( $form->post_content ) : \'\';\r\n	$entries   = wpforms()->entry->get_entries( array( \'form_id\' => absint( $atts[\'id\'] ) ) );\r\n	$disallow  = apply_filters( \'wpforms_frontend_entries_table_disallow\', array( \'divider\', \'html\', \'pagebreak\', \'captcha\' ) );\r\n	$ids       = array();\r\n	ob_start();\r\n	echo \'<table class=\"wpforms-frontend-entries\">\';\r\n		echo \'<thead><tr>\';\r\n			foreach( $form_data[\'fields\'] as $field ) {\r\n				if ( !in_array( $field[\'type\'], $disallow ) ) {\r\n					$ids[] = $field[\'id\'];\r\n					echo \'<th>\' . sanitize_text_field( $field[\'label\'] ) . \'</th>\';\r\n				}\r\n			}\r\n		echo \'</tr></thead>\';\r\n		echo \'<tbody>\';\r\n			foreach( $entries as $entry ) {\r\n				echo \'<tr>\';\r\n				$fields = wpforms_decode( $entry->fields );\r\n				foreach( $fields as $field ) {\r\n					if ( in_array( $field[\'id\'], $ids ) ) {\r\n						echo \'<td>\' . apply_filters( \'wpforms_html_field_value\', wp_strip_all_tags( $field[\'value\'] ), $field, $form_data, \'entry-frontend-table\' );\r\n					}\r\n				}\r\n				echo \'</tr>\';\r\n			}\r\n		echo \'</tbody>\';\r\n	echo \'</table>\';\r\n	$output = ob_get_clean();\r\n	return $output;\r\n}\r\nadd_shortcode( \'wpforms_entries_table\', \'wpf_dev_entries_table\' );', 'Donars', '', 'inherit', 'closed', 'closed', '', '619-revision-v1', '', '', '2018-09-04 06:29:40', '2018-09-04 06:29:40', '', 619, 'http://localhost/wordpress/blog/2018/09/04/619-revision-v1/', 0, 'revision', '', 0),
